import React, { useState } from "react";
import ResultArea from "./ResultArea";
import ClearArea from "./ClearArea";
import Operators from "./Operators";
import Numbers from "./Numbers";

function App(){

    const [user_input,setstate] = useState("");

    let getUserInput = (val) => {
        console.log(val);
        setstate(val);
    }

    function getOperator(operatorVal){
        console.log(operatorVal);
        setstate(prevVal=>{
            return prevVal+operatorVal;
        })
    }

    return (
    <div className="main-div">
        <ResultArea userInput={user_input} />
        <ClearArea />
        <Operators getOperatorAction={getOperator} />
        <Numbers getUserInputAction={getUserInput} />
      
    </div>);
}

export default App;
